package com.tg.cg2023.sf2;

import java.math.BigDecimal;
import java.util.Scanner;

public class CandidateCodeJ7 {

	private final static int MOD = (int) (1e9 + 7);

	private static long dp[] = null;

	public static void calculatePossibleArrayCount(String s, int k) {
		dp = new long[s.length() + 1];
		int[] result = new int[s.length()];

		long c = 0;
		boolean start = true;
		String[] ssArr = s.split("|");
		for (int i = 0; i < ssArr.length; i++) {
			result[i] = Integer.parseInt(ssArr[i]);
			if (result[i] > k) {
				c = 0;
				start = false;
				break;
			}
		}
		if (start)
			c = calculatePossibleArrayCount(result, s, 0, s.length(), k);
		System.out.println((c % MOD));
	}

	public static long calculatePossibleArrayCount(int[] s, String ss, int idx, int n, int k) {

		if (idx < n && s[idx] == 0)
			return 0;

		if (idx >= n) {
			return 1;
		}

		if (dp[idx] != 0) {
			return dp[idx];
		}

		long ways = 0;

		// Pick single
		if (s[idx] != 0)
			ways = calculatePossibleArrayCount(s, ss, idx + 1, n, k);

		if (idx + 1 < n) {
			for (int index = 2; index <= (n - idx); index++) {
				if (idx + index <= n) {
					BigDecimal ii = new BigDecimal(ss.substring(idx, idx + index));
					if ((ii.compareTo(new BigDecimal(1)) == 1 || ii.compareTo(new BigDecimal(1)) == 0)
							&& (ii.compareTo(new BigDecimal(k)) == -1 || ii.compareTo(new BigDecimal(k)) == 0))
						ways += calculatePossibleArrayCount(s, ss, idx + index, n, k);
				}
			}
		}

		return dp[idx] = ways % MOD;
	}

	public static void main(String args[]) throws Exception {
		try (Scanner sc = new Scanner(System.in)) {
//			int n = sc.nextInt();
//			int k = sc.nextInt();
//			String str = sc.next();
//			
			int n = 3;// sc.nextInt();
//			int k = 10000000;// sc.nextInt();
//			String str = "12345";

			int k = 300;// sc.nextInt();
			String str = "1200120";

			// String str =
			// "";
			// String str =
			// "";//
			// sc.next();

			while (str.startsWith("0")) {
				str = str.substring(1);
			}
			calculatePossibleArrayCount(str, k);
		} finally {
		}
		// Write code here

	}

}
